import React from "react";
import styled from "styled-components";

const Projects = () => {
  return (
    <Container>
      <h1 id="projects">Projetos</h1>
      <a href="https://buscamilhas.com/" target="_blank">
        <h2> Busca Milhas: Payment Hub Admin</h2>
      </a>

      <Wrapper>
        <p>
          Como Frontend atuei desenvolvendo uma dashboard administrativa para
          uma API de pagamentos (Payment Hub) utilizada pelo{" "}
          <a href="https://buscamilhas.com/" target="_blank">
            Busca Milhas
          </a>{" "}
          que faz o gerenciamento de clientes, usuários e pagamentos de planos e
          assinaturas. O projeto foi desenvolvido utilizando <b>React</b> com
          <b> Typescript</b>, <b>NextJS</b> e <b>Chakra UI</b> para estilização,
          além de seguir o modelo de Atomic Design para criação do Design System
          do projeto e Jest para realização de testes unitários.
          <br /> Acesse o projeto em:{" "}
        </p>
        <a href="https://buscamilhas.com/" target="_blank">
          <Img
            src="https://i.ibb.co/QNrgTF0/busca-Logo.jpg"
            alt="The project miniature"
          />
        </a>
      </Wrapper>
      <a href="https://buscamilhas.com/lp/?" target="_blank">
        <h2>
          {" "}
          Busca Milhas Start: Microsserviço de cancelamento de recorrência{" "}
        </h2>
      </a>

      <Wrapper>
        <p>
          Como Backend desenvolvi em pair programming um microsserviço que cria
          um fluxo de cancelamento através de questionários customizados a
          partir de uma dashboard, para confirmar o cancelamento da assinatura
          de clientes do Busca Milhas Start. Iniciamos o projeto levantando os requisitos e analisando as regras de negócio, para começarmos a modelagem do banco de dados. O projeto foi desenvolvido utilizando
          conceitos de Clean Archtechture e seguindo princípios de Domain Driven
          Design. Foi desenvolvido em Node, utilizando NestJS com TypeORM e
          MySQL como Banco de dados e também Jest para os testes unitários.
        </p>
        <a href="https://buscamilhas.com/lp/?" target="_blank">
          <Img
            src="https://i.ibb.co/6gxVJBy/buscastart.jpg"
            alt="The project miniature"
          />
        </a>
      </Wrapper>
      <h2>Movie Search App</h2>

      <Wrapper>
        <p>
          Aplicação simples em react native que permite buscar por um filme e
          exibir informações sobre ele. Api utilizada:
          https://www.themoviedb.org/.
        </p>
        <Img
          src="https://raw.githubusercontent.com/joao21dev/movieSearchAppRN/master/assets/movieApp.gif"
          alt="The project miniature"
        />
      </Wrapper>
    </Container>
  );
};

const Container = styled.div`
  margin-top: 3rem;
  margin-inline: 43vh;

  @media (max-width: 768px) {
    margin: 5vh;
  }

  a {
    cursor: "pointer";
  }

  h1 {
    color: #5434af;
  }

  h2 {
    color: #6e58ad;
  }

  p {
    color: #586063;
    line-height: 1.7;
    letter-spacing: 2px;
    font-weight: 300;
  }
`;

const Wrapper = styled.div`
  display: flex;
  flex-direction: row;

  @media (max-width: 768px) {
    flex-direction: column;
  }
`;

const Img = styled.img`
  max-width: 32%;
  height: 16rem;
  border-radius: 5px;
  margin-left: 3rem;

  @media (max-width: 768px) {
    max-width: 15rem;
  }
`;

export default Projects;
